import React from 'react'
import hamradio from 'hamradio'
import {CompactPicker} from 'react-color'
import _ from 'lodash'

import LabelLayout from './basicComponents/LabelLayout'
import FlexRow from './basicComponents/FlexRow'
import Selector from './basicComponents/Selector'
import IconButton from './basicComponents/IconButton'
import TextButton from './basicComponents/TextButton'

import viewconfig from './utils/viewconfig'
import tracks from './utils/tracks'

import './TracksMenu.css'


class TracksMenu extends React.Component {
  constructor (props) {
    super(props);

    this.subscriptions = this.makeSubscriptions()

    this.state = {
      editTileset: null,
      editTrack: null,
      editConstant: this.blankConstant(),
      valueScaleMax: undefined,
      valueScaleMin: undefined
    }
	}

  componentWillUnmount() {
    this.subscriptions.forEach(sub => sub.unsubscribe())
  }

  makeSubscriptions() {
    return [
      hamradio.subscribe(
        'track/modify',
        (name, track) => {
          if (this.state.editTileset && this.state.editTileset.uid in track.tilesets) {
            this.setState({editTileset: track.tilesets[this.state.editTileset.uid]})
          }
        }
      ),
      hamradio.subscribe(
        'track/remove',
        (name, track) => {
          if (this.state.editTileset && track.uid === this.state.editTileset.uid) {
            this.setState({editTileset: null, editTrack: null})
          }
        }
      )
    ]
  }

  trackName = (track, tileset) => {
    let tilesetName = tileset.options.name || tileset.uid
    return `Track ${track.order}: ${tilesetName}`
  }

  toggleVisibility = (track, global, focus) => {
    hamradio.publish('track/modify', {...track, global, focus})
  }

  editTileset = (track, tileset) => {
    this.setState({
      editTileset: _.cloneDeep(tileset),
      editTrack: _.cloneDeep(track),
      valueScaleMax: tileset ? tileset.options.valueScaleMax : undefined,
      valueScaleMin: tileset ? tileset.options.valueScaleMin : undefined
    })
  }

  removeTrack = (track, tileset) => {
    hamradio.publish('tileset/remove', [track.uid, tileset.uid])
  }

  setTrackValue = (key, value) => {
    const tileset = _.merge({}, this.state.editTileset, {[key]: value})
    hamradio.publish('tileset/modify', [this.state.editTileset.uid, tileset])
  }

  setOptionValue = (option, value) => {
    const tileset = _.merge({}, this.state.editTileset, {options: {[option]: value}})
    if (value === undefined) { // override lodash's behavior of ignoring undefined values
      tileset.options[option] = undefined
    }
    hamradio.publish('tileset/modify', [this.state.editTrack.uid, tileset])
  }

  setPrimaryColor = (color) => {
    let options = tracks.options[this.state.editTileset.type]({}, color)
    const tileset = _.merge({}, this.state.editTileset, {options})
    hamradio.publish('tileset/modify', [this.state.editTrack.uid, tileset])
  }

  blankConstant = () => {
    return {
      color: '#555555',
      opacity: 1.0,
      label: '',
      labelPosition: 'right',
      labelColor: '#555555',
      LabelOpacity: 1.0,
      value: 1
    }
  }

  addConstant = () => {
    let options = _.cloneDeep(this.state.editTileset.options)
    if (options.constIndicators) {
      options.constIndicators.push(this.state.editConstant)
    } else {
      options.constIndicators = [this.state.editConstant]
    }
    const tileset = _.merge({}, this.state.editTileset, {options})
    hamradio.publish('tileset/modify', [this.state.editTrack.uid, tileset])
    this.setState({editConstant: this.blankConstant()})
  }

  removeConstant = (index) => {
    let options = _.cloneDeep(this.state.editTileset.options)
    options.constIndicators.splice(index, 1)
    const tileset = tracks.merge(this.state.editTileset, {options})
    hamradio.publish('tileset/modify', [this.state.editTrack.uid, tileset])
    this.setState({editConstant: this.blankConstant()})
  }

  changeType = (type) => {
    let primaryColor = tracks.getPrimaryColor(this.state.editTileset)
    let options = tracks.options[type]({}, primaryColor)
    const tileset = _.merge({}, this.state.editTileset, {type, options})
    hamradio.publish('tileset/remove', [this.state.editTrack.uid, tileset.uid])
    hamradio.publish('tileset/add', [this.state.editTrack.uid, tileset])
  }

  trackOverview = () => {
    return (
      <div>
  			<label>Tracks</label>
        {viewconfig.sortTracks(Object.values(this.props.tracks)).map(track => (
          <div key={track.uid}>
            <LabelLayout label={`Track ${track.order}`}
              inner=<div>
                <TextButton
                  onClick={() => this.toggleVisibility(track, !track.global, track.focus)}
                  text="Global"
                  backgroundColor={track.global ? 'black' : 'white'}
                  color={track.global ? 'white' : 'grey'}
                />
                <TextButton
                  onClick={() => this.toggleVisibility(track, track.global, !track.focus)}
                  text="Focus"
                  backgroundColor={track.focus ? 'black' : 'white'}
                  color={track.focus ? 'white' : 'grey'}
                />
              </div>
            />
            <ul>
              {Object.values(track.tilesets).map(tileset => (
                <li key={tileset.uid}>
                  <IconButton icon="eye" backgroundColor={tracks.getPrimaryColor(tileset)} color="white" onClick={() => this.editTileset(track, tileset)}/>
                  {this.props.allowDeletion
                    ? <IconButton icon="trash" backgroundColor={tracks.getPrimaryColor(tileset)} color="white" onClick={() => this.removeTrack(track, tileset)}/>
                    : null
                  }
                  <label>{tileset.options.name || tileset.uid}</label>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
		)
  }

  trackTypeBlock = () => {
    return (
      <LabelLayout label="Track Type"
        inner=<div>
          <Selector
            value={{value: this.state.editTileset.type, label: this.state.editTileset.type}}
            onChange={option => this.changeType(option.value)}
            options={tracks.trackDataTypes[this.state.editTileset.datatype]
              .concat(this.state.editTileset.filetype === 'bigwig' ? ['range'] : [])
              .map(type => ({value: type, label: type}))}
          />
        </div>
      />
    )
  }

  primaryColorBlock = () => {
    return (
      <div>
        <label>Primary Color</label>
        <div className="ColorPicker">
          <CompactPicker color={tracks.getPrimaryColor(this.state.editTileset)} onChange={color => this.setPrimaryColor(color.hex)}/>
        </div>
      </div>
    )
  }

  rangeResolutionBlock = () => {
    return (
      <div>
        <LabelLayout label="Resolution"
          inner=<input type="number" name="resolution" min="1" max="10"
            value={this.state.editTileset.options.resolution || 1} onChange={e => this.setOptionValue('resolution', e.target.value)}
          />
        />
        <LabelLayout label="Display as"
          inner=<div>
            <Selector
              value={{value: this.state.editTileset.options.mode, label: this.state.editTileset.options.mode}}
              onChange={option => this.setOptionValue('mode', option.value)}
              options={[
                {value: 'minMax', label: 'minMax'},
                {value: 'whisker', label: 'whisker'}
              ]}
            />
          </div>
        />
      </div>
    )
  }

  constantBlock = () => {
    return (
      <div>
        <label>Constants</label>
        <ul>
          {this.state.editTileset.options.constIndicators
            ? this.state.editTileset.options.constIndicators.map((ruler, index) => (
              <li key={index}>
                <LabelLayout label={`${ruler.label}: ${ruler.value}`}
                  inner=<IconButton onClick={() => this.removeConstant(index)} icon="minus"/>
                />
              </li>
            ))
            : null
          }
          <li>
            <FlexRow inner={[
              (<label key="llabel">Label:</label>),
              (<input key="ilabel" type="text" name="constant-label" style={{maxWidth:80}}
                value={this.state.editConstant.label} onChange={e => this.setState({editConstant: {...this.state.editConstant, label: e.target.value}})}
              />),
              (<label key="lvalue">Value:</label>),
              (<input key="ivalue" type="number" name="constant-value" style={{maxWidth:80}}
                value={this.state.editConstant.value} onChange={e => this.setState({editConstant: {...this.state.editConstant, value: e.target.value}})}
              />),
              (<IconButton key="accept" onClick={() => this.addConstant()} icon="plus" backgroundColor="black" color="white"/>)
            ]}/>
          </li>
        </ul>
      </div>
    )
  }

  yBoundsBlock = () => {
    let debouncedOptions = _.debounce(this.setOptionValue, 500, {trailing: true})
    let update = (option, value) => {
      debouncedOptions(option, value)
      this.setState({[option]: value})
    }
    let toggleAuto = (option) => {
      return () => {
        if (this.state[option] === undefined) {
          this.setOptionValue(option, 0.0)
          this.setState({[option]: 0.0})
        } else {
          this.setOptionValue(option, undefined)
          this.setState({[option]: undefined})
        }
      }
    }
    return (
      <div>
        <label>Y-Axis Bounds</label>
        <ul>
          <li>
            <FlexRow label="Max"
              inner={[
                <label key="label">Max</label>,
                <TextButton key="button" text={this.state.valueScaleMax === undefined ? "Auto" : "Manual"}
                  onClick={toggleAuto('valueScaleMax')}
                  backgroundColor={this.state.valueScaleMax === undefined ? 'black' : 'white'}
                  color={this.state.valueScaleMax === undefined ? 'white' : 'black'}
                />,
                this.state.valueScaleMax === undefined ? null :
                  <input key="input" type="number" name="maxY" value={this.state.valueScaleMax}
                    onChange={e => update('valueScaleMax', e.target.value)} />
              ]}
            />
          </li>
          <li>
            <FlexRow
              inner={[
                <label key="label">Min</label>,
                <TextButton key="button" text={this.state.valueScaleMin === undefined ? "Auto" : "Manual"}
                  onClick={toggleAuto('valueScaleMin')}
                  backgroundColor={this.state.valueScaleMin === undefined ? 'black' : 'white'}
                  color={this.state.valueScaleMin === undefined ? 'white' : 'black'}
                />,
                this.state.valueScaleMin === undefined ? null :
                  <input key="input" type="number" name="maxY" value={this.state.valueScaleMin}
                    onChange={e => update('valueScaleMin', e.target.value)} />
              ]}
            />
          </li>
        </ul>
      </div>
    )
  }

  trackEdit = () => {
    return (
      <div>
  			<label>Edit {this.trackName(this.state.editTrack, this.state.editTileset)}</label>
        <div>
          <br/>
          {this.trackTypeBlock()}
          <br/>
          {this.primaryColorBlock()}
          {this.state.editTileset.type === 'range' ? (<br/>) : null}
          {this.state.editTileset.type === 'range' ? this.rangeResolutionBlock() : null}
          <br/>
          {this.constantBlock()}
          <br/>
          {this.yBoundsBlock()}
          <br/>
          <TextButton onClick={() => this.editTileset(null, null)} text="Done" />
        </div>
      </div>
    )
  }

	render () {
		return !this.state.editTileset ? this.trackOverview() : this.trackEdit()
	}
}


export default TracksMenu;
