import React from 'react'

export default function FlexRow (props) {
  const divStyle = Object.assign({
    display: "flex",
    flexFlow: "row",
    justifyContent: "flex-start",
    alignItems: "center"
  }, props.divStyle || {})
  return (
    <div style={divStyle}>
      {props.inner}
    </div>
  )
}
