import React from 'react'
import FlexRow from './FlexRow'

export default function LabelLayout (props) {
  const labelStyle = {
    marginTop: "5px",
    marginBottom: "5px",
    padding: "1px",
    fontWeight: "700",
    maxWidth: "50%"
  }
  return (
    <FlexRow inner={[
      (<label key="label" style={labelStyle}>{props.label}</label>),
      (<div key="div">{props.inner}</div>)
    ]}/>
  )
}
