import React from 'react'

export default function TextButton (props) {
    const style = {
      backgroundColor: props.backgroundColor || 'white',
      color: props.color || 'black',
    	margin: "0px 5px",
    	padding: "1px 10px"
    }
    return (
      <button
        style={style}
        className="btn btn-default"
        onClick={props.onClick}
      >
        {props.text}
      </button>
    )
}
