function horizontalPointOptions (overrides) {
  return Object.assign({
    labelColor: "black",
    labelPosition: "topLeft",
    axisPositionHorizontal: "right",
    pointColor: "red",
    pointSize: 2,
    valueScaling: "linear",
    trackBorderWidth: 1,
    trackBorderColor: "grey",
    labelTextOpacity: 0.4,
    name: "template_label"
  }, overrides)
}

function track (overrides, options) {
  return Object.assign(
    {
      name: "",
      server: "", // FUTURE CONFIG
      tilesetUid: "",
      uid: "",
      type: "horizontal-point",
      height: 38,
      position: "top"
    },
    overrides,
    {
      options: {
        showTooltip: true,
        ...options
      }
    }
  )
}

